% display high-order differenced carrier phase
function GUI_Results_DispDiffPhaseData(ps,status_record,diff_record,vTimeStr,length_sequence,order_diff)

%inputs:
% ps                            --> sat id
% status_record         -->status generated by the associated method (cycle-slip free, cycle-slip detected, incomplete, interrupted)
% diff_record              --> high-order differenced phase data generated (for each satellite and each epoch)
% vTimeStr                  --> string regarding the UTC datum and time
% length_sequence    --> the length of the queue containing the differenced phase data
% order_diff                --> order of differencing

Constants;

totalepoch=size(status_record,2);

% show the result of each epoch using different symbols
for j=1:1:totalepoch,
    % check the status of each epoch in order to determine the symbols to
    % be shown
    switch status_record(ps,j)
        case const_interrupt
            plot(j,0,'co', 'MarkerEdgeColor','k', 'MarkerFaceColor','c', 'MarkerSize',12); hold on
        case const_incomplete
            plot(j,0,'r+'); hold on
        case const_cycleclipfree
            plot(j,diff_record(ps,j),'k.','LineWidth',3); hold on
        case const_detected
            plot(j,diff_record(ps,j),'rp', 'MarkerEdgeColor','r', 'MarkerFaceColor','r', 'MarkerSize',12); hold on
    end
end

titlestr=sprintf('High-order differenced carrier phase measurements ( order=%d and length of data series=%d )',order_diff,length_sequence);
title(titlestr)
xlim([1 totalepoch])

% xlabel with time display
aa=get(gca,'XTick')    ;
clear strs
for i=1:1:length(aa),
    if aa(i)==0, aa(i)=1; end
    epoch_str=vTimeStr{aa(i)};
    datumstr=epoch_str(1:10);
    timestr=epoch_str(11:20);
    strs{i}=timestr;
end
set(gca,'XTickLabel',strs )
xlabel('Time')
ylabel('Differenced carrier phase [cycles]')