% show the ratio of standard deviations of 

function GUI_Results_DispStdDiffPhase2(ps,status_record,mStdOld,mStdNew,threshold,vTimeStr,length_sequence,order_diff)

%inputs:
% ps                            --> sat id
% status_record         -->status generated by the associated method (cycle-slip free, cycle-slip detected, incomplete, interrupted)
% mStdOld                 --> std of phase data without the one on current epoch
% mStdNew              -->  std of phase data with the one on current epoch
% vTimeStr                  --> string regarding the UTC datum and time
% length_sequence    ---> length of the queue containing the differenced
% threshold                 --->      threshold for maximal allowable ratio 
% order_diff              --->  order of difference (only for figure title)

Constants;
totalepoch=size(status_record,2);
 
upper_bound_value=ones(1,totalepoch)*threshold;
plot(1:totalepoch,+upper_bound_value,'g','LineWidth',2); hold on
for j=1:1:totalepoch,    
    switch status_record(ps,j)
        case const_interrupt
            plot(j,0,'co', 'MarkerEdgeColor','k', 'MarkerFaceColor','c', 'MarkerSize',12); hold on
        case const_incomplete
            plot(j,0,'r+'); hold on
        case const_cycleclipfree,
            plot(j,mStdNew(ps,j)/mStdOld(ps,j),'k.','LineWidth',3); hold on
        case const_detected
            plot(j,mStdNew(ps,j)/mStdOld(ps,j),'rp', 'MarkerEdgeColor','r', 'MarkerFaceColor','r', 'MarkerSize',12); hold on
    end
end

titlestr=sprintf('Ratio of standard deviations with/without current carrier phase (order =%d and length of data series=%d)',order_diff,length_sequence);
title(titlestr)
xlim([1 totalepoch])

% show X axis with  time 
aa=get(gca,'XTick')    ;
clear strs
for i=1:1:length(aa),
    if aa(i)==0, aa(i)=1; end
    epoch_str=vTimeStr{aa(i)};
    datumstr=epoch_str(1:10);
    timestr=epoch_str(11:20);
    strs{i}=timestr;
end
set(gca,'XTickLabel',strs )
xlabel('Time')
ylabel('Differenced carrier phase [cycles]')